function nav(){const e=document.getElementsByClassName("nav-section");for(let t of e)t.onmouseover=function(){Array.from(e).forEach(e=>{document.getElementById(`sub-pages-container-${e.id}`).style.display="none"});let t=document.getElementById(`sub-pages-container-${this.id}`);t.style.display="block",t.classList.add("animate__animated","animate__fadeIn"),document.getElementById("gb-main").classList.add("blurPage"),document.getElementById("gb-footer").classList.add("blurPage")};const t=document.getElementsByClassName("sub-pages-container");for(let e of t)e.onmouseover=function(){document.getElementById(this.id).style.display="block",document.getElementById("gb-main").classList.add("blurPage"),document.getElementById("gb-footer").classList.add("blurPage")},e.onmouseout=function(){let e=document.getElementById(this.id);document.getElementById(this.id).style.display="none",document.getElementById("gb-main").classList.remove("blurPage"),document.getElementById("gb-footer").classList.remove("blurPage")}}nav()