function showSubPagesContainer(){}const navSections=document.getElementsByClassName("nav-section");for(let e of navSections)e.onmouseover=function(){Array.from(navSections).forEach(e=>{document.getElementById(`sub-pages-container-${e.id}`).style.display="none"});let e=document.getElementById(`sub-pages-container-${this.id}`);e.style.display="block",e.classList.add("animate__animated","animate__fadeIn"),document.getElementById("gb-main").classList.add("blurPage"),document.getElementById("gb-footer").classList.add("blurPage")};const subPagesContainer=document.getElementsByClassName("sub-pages-container");for(let e of subPagesContainer)e.onmouseover=function(){document.getElementById(this.id).style.display="block",document.getElementById("gb-main").classList.add("blurPage"),document.getElementById("gb-footer").classList.add("blurPage")},e.onmouseout=function(){let e=document.getElementById(this.id);document.getElementById(this.id).style.display="none",document.getElementById("gb-main").classList.remove("blurPage"),document.getElementById("gb-footer").classList.remove("blurPage")}