function tableMeger(){const e=document.querySelectorAll('table tr');e.forEach((t,n)=>{const s=t.querySelectorAll('td');s.forEach((t,o)=>{const s=t.textContent.trim();if(s.startsWith('^')){let i=1;for(let t=n+1;t<e.length;t++){const a=e[t].querySelectorAll('td')[o];if(a==void 0||a.textContent.trim()==='')console.log(s,i),i++;else break}i>1&&(t.rowSpan=i,t.textContent=t.textContent.slice(1))}})});const t=document.querySelectorAll('td');t.forEach(e=>{e.textContent.trim()===''&&e.remove()})}tableMeger()