function tableMeger(){const e=document.querySelectorAll('table tr');e.forEach((t,n)=>{const s=t.querySelectorAll('td');s.forEach((t,s)=>{const o=t.textContent.trim();if(o.startsWith('^')){let o=1;for(let t=n+1;t<e.length;t++){const i=e[t].querySelectorAll('td')[s];if(i==void 0||i.textContent.trim()==='')o++;else break}o>1&&(t.rowSpan=o,t.textContent=t.textContent.slice(1))}})});const t=document.querySelectorAll('td');t.forEach(e=>{e.textContent.trim()===''&&e.remove()})}tableMeger()